/*!
 *  Bootstrap Modal plugin remote option compatible script
 *    for Bootstrap v3.3 or later
 */
(function(a){a('a[data-toggle="modal"]').on("click",function(){var b=a(this).attr("data-target");if(!a(b).length){a('<div id="'+b.replace("#","")+'" class="modal fade" role="dialog" />').appendTo("body")}a(b).empty();a.get(a(this).attr("href"),function(c){a(b).empty().append(a(c)).modal()});return false})}(window.jQuery));